export const initialState = {
  isLoggedIn : false,
  user: null,
  signUpData : {},
  loginData : {},
}

export const loginAction = (data) => {
  return {
    type: "LOG_IN",
    data,
  }
}

export const logoutAction = () => {
  return {
    type: "LOG_OUT",
  }
}

const reducer = (state = initialState,action) => {
  console.log(action.type);
  console.log(action.data);
  switch(action.type) {
    
    case 'LOG_IN':
      return {
        ...state,
        user: {
          ...state.user,
          isLoggedIn:true,
          user:action.data,
        },
      };
    case 'LOG_OUT':
    return {
      ...state,
      user: {
        ...state.user,
        isLoggedIn:false,
        user:null,
      }
    }
    default:
      return state;
  }
}

export default reducer;
