import React, {useState} from 'react';
import PropTypes from 'prop-types'
import Slick from 'react-slick'
import styled, { createGlobalStyle} from 'styled-components';
import { CloseOutlined } from '@ant-design/icons/lib/icons';

const Overlay = styled.div`
  position: fixed;
  z-index: 5000;
  top:0;
  left:0;
  right:0;
  bottom:0;
`;
const Header = styled.header`
  height:44px;
  background: white;
  position: relative;
  padding: 0;
  text-align: center;

  & h1 {
    margin: 0;
    font-size: 17px;
    color: #333;
    line-height: 44px;
  }
`;  
const CloseBtn = styled(CloseOutlined)`
    position: absolute;
    right: 0;
    top: 0;
    padding: 10px;
    line-height: 14px;
    cursor: pointer;
`
const SlickWrapper = styled.div`
  height: calc(100% - 44px);
  background: #090909; 
`;
const ImgWrapper = styled.div`
  padding: 32px;
  text-align: center;

  & img {
    margin: 0 auto;
    max-height: 750px;
  }
  `;
const Indicator = styled.div`
  text-align: center;

  & > div {
    width: 750px;
    height: 30px;
    line-height: 30px;
    border-radius : 15px;
    background: #313131;
    display: inline-block;
    text-align: center;
    color: white;
    font-size: 15px;
  }
`;
const Global = createGlobalStyle`
  .slick-slide {
    display: inline-block;
  }
`

const ImagesZoom = ({images,onClose}) => {
  const [currentState, setCurrentSlide] = useState(0);
  return (
    <Overlay>
      <Global />
      <Header>
        <h1>상세 이미지</h1>
        <button onClick={onClose} >X</button>
      </Header>
      <SlickWrapper>
        <div>
          <Slick 
            initialSlide={0}
            afterChange={(slide) => setCurrentSlide(slide)}
            infinite // 마지막에서 다시 첫번째로
            arrows={false}
            slidesToShow={1}
            slidesToScroll={1}
          >
            {images.map((v) => (
              <ImgWrapper key={v.src}>
                <img src={v.src} alt={v.src} />
              </ImgWrapper>
            ))}
          </Slick>
        </div>
      </SlickWrapper>
    </Overlay>
  );
};

ImagesZoom.propTypes = {
  images: PropTypes.arrayOf(PropTypes.object).isRequired,
  onClose: PropTypes.func.isRequired,
}

export default ImagesZoom;
