import shortid from "shortid";

export const initialState = {
  logInLoading: false, // 로그인 시도 중
  logInDone : false,
  logInError: null, 

  logOutLoading: false,  // 로그아웃 시도 중
  logOutDone: false,
  logOutError: null,

  signUpLoading: false,  // 회원가입 시도 중
  signUpDone: false,
  signUpError: null,

  changeNicknameLoading: false,  // 닉네임 변경 시도 중
  changeNicknameDone: false,
  changeNicknameError: null,
  
  me: null,
  signUpData : {},
  loginData : {},
}

export const LOG_IN_REQUEST = 'LOG_IN_REQUEST';
export const LOG_IN_SUCCESS = 'LOG_IN_SUCCESS';
export const LOG_IN_FAILURE = 'LOG_IN_FAILURE';

export const LOG_OUT_REQUEST = 'LOG_OUT_REQUEST';
export const LOG_OUT_SUCCESS = 'LOG_OUT_SUCCESS';
export const LOG_OUT_FAILURE = 'LOG_OUT_FAILURE';

export const SIGN_UP_REQUEST = 'SIGN_UP_REQUEST';
export const SIGN_UP_SUCCESS = 'SIGN_UP_SUCCESS';
export const SIGN_UP_FAILURE = 'SIGN_UP_FAILURE';

export const CHANGE_NICKNAME_REQUEST = 'CHANGE_NICKNAME_REQUEST';
export const CHANGE_NICKNAME_SUCCESS = 'CHANGE_NICKNAME_SUCCESS';
export const CHANGE_NICKNAME_FAILURE = 'CHANGE_NICKNAME_FAILURE';

export const FOLLOEW_REQUEST = 'FOLLOEW_REQUEST';
export const FOLLOEW_SUCCESS = 'FOLLOEW_SUCCESS';
export const FOLLOEW_FAILURE = 'FOLLOEW_FAILURE';

export const UNFOLLOEW_REQUEST = 'UNFOLLOEW_REQUEST';
export const UNFOLLOEW_SUCCESS = 'UNFOLLOEW_SUCCESS';
export const UNFOLLOEW_FAILURE = 'UNFOLLOEW_FAILURE';

export const ADD_POST_TO_ME = 'ADD_POST_TO_ME';
export const REMOVE_POST_OF_ME = 'REMOVE_POST_OF_ME';

const dummyUser = (data) => ({
  ...data,
  nickname:'김진성',
  id:shortid.generate(),
  Posts: [{id: 1}],
  Followings:[{ nickname:'김진성'},{ nickname:'부부'},{ nickname:'아아'},],
  Followers:[{ nickname:'김진성'},{ nickname:'부부'},{ nickname:'아아'}],

})

export const loginRequestAction = (data) => {
  return {
    type: "LOG_IN_REQUEST",
    data, 
  }
}

export const logoutRequestAction = () => {
  return {
    type: "LOG_OUT_REQUEST",
  }
}

const reducer = (state = initialState,action) => {
  switch(action.type) {
    case LOG_IN_REQUEST:
      return {
        ...state,
        logInLoading:true,
        logInDone: false,
        logInError: null,
      };
    case LOG_IN_SUCCESS:
      return {
        ...state,
        logInLoading:false,
        logInDone:true,
        me: dummyUser(action.data),
      };
    case LOG_IN_FAILURE:
      return {
        ...state,
        logInLoading:false,
        logInError:action.error,
      };
      
    case LOG_OUT_REQUEST:
      return {
        ...state,
        logOutLoading: true,
        logOutDone:false,
        logOutError:null
      }
    case LOG_OUT_SUCCESS:
      return {
        ...state,
        logOutLoading:false,
        logOutDone:true,
        me:null,
      }
    case LOG_OUT_FAILURE:
      return {
        ...state,
        logOutLoading:false,
        logOutError: action.error
      }

    case SIGN_UP_REQUEST:
      return {
        ...state,
        signUpLoading: true,
        signUpDone:false,
        signUpError:null
      }
    case SIGN_UP_SUCCESS:
      return {
        ...state,
        signUpLoading:false,
        signUpDone:true,
      }
    case SIGN_UP_FAILURE:
      return {
        ...state,
        signUpLoading:false,
        signUpError: action.error
      }

    case CHANGE_NICKNAME_REQUEST:
      return {
        ...state,
        changeNicknameLoading: true,
        changeNicknameDone:false,
        changeNicknameError:null
      }
    case CHANGE_NICKNAME_SUCCESS:
      return {
        ...state,
        changeNicknameLoading:false,
        changeNicknameDone:true,
      }
    case CHANGE_NICKNAME_FAILURE:
      return {
        ...state,
        changeNicknameLoading:false,
        changeNicknameError: action.error
      }

    case ADD_POST_TO_ME:
      return {
        ...state,
        me: {
          ...state.me,
          Posts: [{ id: action.data }, ...state.me.Posts]
        } 
      }
    default:
      return state;
  }
}

export default reducer;
