import React,{useCallback, useEffect, useState} from 'react';
import Head from 'next/head'
import AppLayout from '../components/AppLayout';
import FollowList from '../components/FollowList';
import NicknameEditForm from '../components/NicknameEditForm';
import { useDispatch, useSelector } from 'react-redux';
import Router from 'next/router'
import { LOAD_MY_INFO_REQUEST } from '../reducers/user';
import { END } from 'redux-saga';
import axios from 'axios';
import wrapper from '../store/configureStore';
import useSWR from 'swr';

const fetcher = (url) => axios.get(url, { withCredentials: true}).then((result) => result.data);

const Profile = () => {
  const dispatch = useDispatch();
  const { me } = useSelector((state)=> state.user);
  const [ followersLimit, setFollowersLimit] = useState(3);
  const [ followingsLimit, setFollowingsLimit] = useState(3);

  const { data: followersData, error:followerError } = useSWR(`http://localhost:4001/user/followers?limit=${followersLimit}`, fetcher);
  const { data: followingsData, error:followingError } = useSWR(`http://localhost:4001/user/followings?limit=${followingsLimit}`, fetcher);

  useEffect(() => {
    if(!(me && me.id)) {
      Router.push('/');
    }
  }, [me&& me.id]);

  const loadMoreFollowings = useCallback(() => {
    setFollowersLimit((prev) => prev + 3);
  },[]);
  const loadMoreFollowers = useCallback(() => {
    setFollowingsLimit((prev) => prev + 3);
  },[]);
  
  if(!me) {
    return '내 정보 로딩중...';
  }

  if(followerError || followingError) {
    console.error(followerError || followingError);
    return <div>팔로잉/팔로워 로딩 중 에러가 발생합니다.</div>;
  }

  
  return (
    <>
      <Head>
        <title>프로필 | NodeBird</title>
      </Head>
      <AppLayout>
        <NicknameEditForm />
        <FollowList header="팔로잉" data={followingsData} onClickMore={loadMoreFollowers} loading={!followingsData && !followersData} />
        <FollowList header="팔로워" data={followersData} onClickMore={loadMoreFollowings} loading={!followingsData && !followersData} />
      </AppLayout>
    </>
  );
};

export const getServerSideProps = wrapper.getServerSideProps(async (context) =>  {
  const cookie = context.req ? context.req.headers.cookie : '';
  axios.defaults.headers.Cookie= '';
  if(context.req && cookie) {
    axios.defaults.headers.Cookie= cookie;
  }
  context.store.dispatch({
    type: LOAD_MY_INFO_REQUEST,
  })
  context.store.dispatch(END)
    await context.store.sagaTask.toPromise();
  
})

export default Profile;
