import axios from 'axios';
import React from 'react';
import { all ,fork, call, put, take} from 'redux-saga/effects';

function logiInAPI() {
  return axios.get("/api/login")
}

function* logIn() {
  try {
    const result =  yield call(logiInAPI);
    yield put({
      type: 'LOG_IN_SUCCESS', 
      data: result.data  
    }) 
  } catch (err) {
    yield put({
      type: 'LOG_IN_FAILURE',
      data : result.data
    })
  }  
}

function* watchLogin() {
  yield take("ADD_POST", logIn);
}

export default function* rootSaga() {
  yield all([
    fork(watchLogin),
  ])
}
